// // These constants represent the RISC-V ELF and the image ID generated by risc0-build.
// // The ELF is used for proving and the ID is used for verification.
use clap::Parser;
use methods::{CONSENSUS_STF_ELF, CONSENSUS_STF_ID};
use ream_consensus::deneb::beacon_state::BeaconState;
use ream_consensus::deneb::beacon_block::BeaconBlock;
use risc0_zkvm::{default_prover, ExecutorEnv};
use snap::raw::Decoder;
use ssz::Decode;

mod beacon_client;
use beacon_client::BeaconNodeHttpClient;

mod cli;
use cli::ProviderArgs;

#[derive(Parser, Debug)]
#[clap(author, version, about, long_about = None)]
struct Args {
    /// Fetch the state and block from the RPC
    #[clap(long)]
    rpc: bool,

    /// Fetch the state and block from local files
    #[clap(long)]
    local: bool,

    #[clap(flatten)]
    provider: ProviderArgs,
}

#[tokio::main]
async fn main() {
    // Initialize tracing. In order to view logs, run `RUST_LOG=info cargo run`
    tracing_subscriber::fmt()
        .with_env_filter(tracing_subscriber::filter::EnvFilter::from_default_env())
        .init();

    let args = Args::parse();

    if args.rpc == args.local {
        eprintln!("Error: You must specify either --rpc or --local");
        std::process::exit(1);
    }

    let pre_state =
        if args.rpc {
            let beacon_client = BeaconNodeHttpClient::new(args.provider.rpc_url.unwrap());

            let head_slot = beacon_client.get_beacon_head_slot().await.unwrap();

            println!("head slot: {:?}", &head_slot);

            //
            // Fetch state by ssz
            //

            let pre_state_ssz = beacon_client.get_beacon_state_ssz(head_slot).await.unwrap();
            // println!("pre-state ssz: {:?}", &pre_state_ssz);

            let pre_state = BeaconState::from_ssz_bytes(&pre_state_ssz);
            println!("pre-state: {:#?}", &pre_state);

            //
            // Fetch state by json
            //

            // let pre_state = beacon_client.get_beacon_state(head_slot).await;
            // println!("pre-state: {:?}", &pre_state);

            // Read and decompress SSZ snappy file
            let ssz_snappy = std::fs::read(format!("host/src/data/basic_block_header/pre.ssz_snappy"))
                .expect("cannot find post-fork test asset");
            let mut decoder = Decoder::new();
            let pre_state_ssz = decoder.decompress_vec(&ssz_snappy).unwrap();
            let pre_state = BeaconState::from_ssz_bytes(&pre_state_ssz).unwrap();

            pre_state
        } else {
            // Read and decompress SSZ snappy file
            let ssz_snappy = std::fs::read(format!("host/src/data/basic_block_header/pre.ssz_snappy"))
                .expect("cannot find post-fork test asset");
            let mut decoder = Decoder::new();
            let pre_state_ssz = decoder.decompress_vec(&ssz_snappy).unwrap();
            let pre_state = BeaconState::from_ssz_bytes(&pre_state_ssz).unwrap();

            pre_state
        };

    // Read and decompress SSZ snappy file
    let block_ssz_snappy = std::fs::read(format!("host/src/data/basic_block_header/block.ssz_snappy"))
        .expect("cannot find block test asset");
    let mut decoder = Decoder::new();
    let block_ssz = decoder.decompress_vec(&block_ssz_snappy).unwrap();
    let block = BeaconBlock::from_ssz_bytes(&block_ssz).unwrap();

    // println!("pre_state: {:?}", &pre_state);
    // println!("block: {:?}", &block);

    //
    // zkVM operations
    //

    // Build the zkVM guest environment
    let env = ExecutorEnv::builder()
        .write(&pre_state)
        .unwrap()
        .write(&block)
        .unwrap()
        .build()
        .unwrap();

    // Run the state transition

    // Obtain the default prover.
    let prover = default_prover();

    // Proof information by proving the specified ELF binary.
    // This struct contains the receipt along with statistics about execution of the guest
    let prove_info = prover.prove(env, CONSENSUS_STF_ELF).unwrap();

    // Extract the receipt.
    let receipt = prove_info.receipt;

    // TODO: Implement code for retrieving receipt journal here.
    // For example:
    let output: BeaconState = receipt.journal.decode().unwrap();

    println!("Receipt journal: {:?}", output);

    // The receipt was verified at the end of proving, but the below code is an
    // example of how someone else could verify this receipt.
    receipt.verify(CONSENSUS_STF_ID).unwrap();
}
